// Decode base64 content from GitHub
const githubContent = Buffer.from($input.first().json.content, 'base64').toString('utf8');
return [{ json: { githubContent, fileName: 'blogPostContent.ts' } }];

// Extract snippet from downloaded file
const snippet = Buffer.from($input.first().json.data, 'base64').toString('utf8');

// Basic validation - check if it contains required elements
if (!snippet.includes('slug') || !snippet.includes('title') || !snippet.includes('date')) {
  throw new Error('Snippet must contain slug, title, and date fields');
}

// Check if it's a blog post snippet
if (snippet.includes('newBlogPost') || snippet.includes('content') || snippet.includes('richContent')) {
  return [{ json: { 
    validSnippet: true, 
    snippet: snippet,
    originalSnippet: snippet,
    fileName: 'blogPostContent-snippet',
    snippetType: 'blogPost'
  } }];
}

// Check if it's a blog preview snippet
if (snippet.includes('newBlogPreview') || snippet.includes('excerpt')) {
  return [{ json: { 
    validSnippet: true, 
    snippet: snippet,
    originalSnippet: snippet,
    fileName: 'blogPreviews-snippet',
    snippetType: 'blogPreview'
  } }];
}

throw new Error('Snippet must be either a blog post or blog preview format');

// Use Python script to extract GitHub content
const { exec } = require('child_process');
const util = require('util');
const execAsync = util.promisify(exec);

const githubContent = $input.first().json.content;
const contentType = 'blogPostContent';

// Call Python script
const pythonScript = './compare_node/extract_github_content.py';
const command = `python3 ${pythonScript} ${contentType} "${githubContent}"`;

try {
  const { stdout, stderr } = await execAsync(command);

  if (stderr) {
    throw new Error(`Python script error: ${stderr}`);
  }

  const result = JSON.parse(stdout);

  if (result.error) {
    throw new Error(result.error);
  }

  return [{ json: { 
    githubContent: result.rawContent,
    extractedContent: result.blogPostContent,
    fileName: 'blogPostContent.ts'
  } }];
} catch (error) {
  throw new Error(`Failed to extract GitHub content: ${error.message}`);
}

// Use Python script to compare content
const { exec } = require('child_process');
const util = require('util');
const execAsync = util.promisify(exec);

const githubContent = $('Extract GitHub Content').item.json.githubContent;
const snippetContent = $('Validate Snippet2').item.json.snippet;
const contentType = 'blogPostContent';

// Call Python script
const pythonScript = './compare_node/compare_content.py';
const command = `python3 ${pythonScript} ${contentType} "${githubContent}" "${snippetContent}"`;

try {
  const { stdout, stderr } = await execAsync(command);

  if (stderr) {
    throw new Error(`Python script error: ${stderr}`);
  }

  const result = JSON.parse(stdout);

  if (result.error) {
    throw new Error(result.error);
  }

  return [{ json: { 
    hasChanges: result.hasChanges,
    existingCount: result.existingCount,
    newCount: result.newCount,
    newPost: result.newPost,
    mergedContent: result.mergedContent,
    message: result.message || null,
    error: result.error || null
  } }];
} catch (error) {
  throw new Error(`Failed to compare content: ${error.message}`);
}

// Parse existing GitHub content and merge with new snippet
const existingContent = $('Extract GitHub Content').item.json.extractedContent;
const newSnippet = $('Validate Snippet2').item.json.snippet;

// Extract blogPostContent array from existing file
const existingMatch = existingContent.match(/export const blogPostContent = (\[.*?\]);/s);
const existingArray = existingMatch ? JSON.parse(existingMatch[1]) : [];

// Extract new blog post from snippet
const snippetMatch = newSnippet.match(/export const newBlogPost = (\{.*?\});/s);
const newBlogPost = snippetMatch ? JSON.parse(snippetMatch[1]) : null;

if (!newBlogPost) {
  throw new Error('Invalid snippet format - could not extract newBlogPost');
}

// Check for duplicate slug
const existingSlugs = existingArray.map(post => post.slug);
if (existingSlugs.includes(newBlogPost.slug)) {
  throw new Error(`Blog post with slug '${newBlogPost.slug}' already exists`);
}

// Merge content
const mergedArray = [...existingArray, newBlogPost];

// Generate updated TypeScript file
const updatedContent = `export const blogPostContent = ${JSON.stringify(mergedArray, null, 2)};`;

return [{ json: { mergedContent: updatedContent, newPostCount: mergedArray.length, addedSlug: newBlogPost.slug } }];

// Check for potential issues in merged content
const mergedContent = $input.first().json.mergedContent;

// Validate TypeScript syntax
if (!mergedContent.includes('export const blogPostContent')) {
  throw new Error('Merged content missing required export statement');
}

// Check for valid JSON structure
try {
  const contentMatch = mergedContent.match(/export const blogPostContent = (\[.*?\]);/s);
  if (contentMatch) {
    JSON.parse(contentMatch[1]);
  }
} catch (error) {
  throw new Error('Invalid JSON structure in merged content');
}

return [{ json: { safeContent: true, contentLength: mergedContent.length } }];

// Log successful merge
const addedSlug = $('Merge Content').item.json.addedSlug;
const newPostCount = $('Merge Content').item.json.newPostCount;

return [{ json: { 
  message: `Successfully merged new blog post: ${addedSlug}`,
  totalPosts: newPostCount,
  timestamp: new Date().toISOString(),
  action: 'merge_success'
} }];

// Log no action needed
return [{ json: { 
  message: 'Content is identical - no update needed',
  timestamp: new Date().toISOString(),
  action: 'no_action'
} }];

// Validate preview snippet format
const snippet = Buffer.from($input.first().json.data, 'base64').toString('utf8');

// Check for required export
if (!snippet.includes('export const newBlogPreview')) {
  throw new Error('Preview snippet must contain "export const newBlogPreview"');
}

// Check for required fields
const requiredFields = ['slug', 'title', 'date', 'excerpt'];
const missingFields = requiredFields.filter(field => !snippet.includes(field));

if (missingFields.length > 0) {
  throw new Error(`Missing required fields: ${missingFields.join(', ')}`);
}

return [{ json: { validSnippet: true, snippet, fileName: 'blogPreviews-snippet' } }];

// Parse existing GitHub content and merge with new preview snippet
