  timestamp: new Date().toISOString(),
  action: 'preview_merge_success'
} }];

// Log no action needed for preview
return [{ json: { 
  message: 'Preview content is identical - no update needed',
  timestamp: new Date().toISOString(),
  action: 'preview_no_action'
} }];

